INSERT INTO `dt_article` VALUES (65, 10, 27, '', '30个IOS7风格简洁线条图标PSD素材', '', '/upload/201504/19/201504190047488297.png', '30个IOS7风格简洁线条图标PSD素材', 'IOS7,图标,PSD素材', 'PSD格式，含JPG预览图，关键字：图标,PSD素材,IOS7,线条,简洁,标签,云,音乐,用户,邮件,查找,刷新,喜欢,定位,苹果', '', 'PSD格式，含JPG预览图，关键字：图标,PSD素材,IOS7,线条,简洁,标签,云,音乐,用户,邮件,查找,刷新,喜欢,定位,苹果', '<p>\r\n	PSD格式，含JPG预览图，关键字：图标,PSD素材,IOS7,线条,简洁,标签,云,音乐,用户,邮件,查找,刷新,喜欢,定位,苹果\r\n</p>\r\n<p align=\"center\">\r\n	<img alt=\"\" src=\"http://img.lanrentuku.com/img/allimg/1310/13822298688602.jpg\" />\r\n</p>', 99, 2, 0, 1, 0, 1, 0, 0, 1, 'admin', '2015-04-19 00:48:05', '2016-12-06 17:04:16');
