INSERT INTO `dt_article` VALUES (83, 8, 37, '', '奔跑吧兄弟', '', '/upload/201504/19/201504191612121323.jpg', '奔跑吧兄弟', '视频,视频分享,视频搜索,视频播放', '奔跑吧兄弟是浙江卫视全新推出的大型户外竞技真人秀节目，节目于2014年8月28日正式开机。', '', '奔跑吧兄弟是浙江卫视全新推出的大型户外竞技真人秀节目，节目于2014年8月28日正式开机，10月10日将登陆浙江卫视周五黄金档。该节目原型为韩版《runningman》，中国改名为《奔跑吧兄弟》由浙江卫视和韩版《Running Man》制作团队SBS联合制作。', '奔跑吧兄弟是浙江卫视全新推出的大型户外竞技真人秀节目，节目于2014年8月28日正式开机，10月10日将登陆浙江卫视周五黄金档。该节目原型为韩版《runningman》，中国改名为《奔跑吧兄弟》由浙江卫视和韩版《Running Man》制作团队SBS联合制作。', 99, 2, 0, 1, 0, 1, 0, 0, 1, 'admin', '2015-04-19 16:12:20', '2016-12-06 17:04:16');
